insert into AgreementsItem(item, agree_title_item) values
('Інтернет-сайт — інформація, розміщена в інформаційно-телекомунікаційній мережі за певними мережевими адресами, в сукупності з комплексом виключних прав (на доменні імена, бази даних, розміщений контент і програми для електронних обчислювальних машин), здійснення яких через спеціальний комп''ютерний код забезпечує доступ до такої інформації.', 1),
('Адміністрація сайту — власник/його уповноважений представник, що володіє правами на володіння, використання та управління Інтернет-сайтом, зазначеним у пункті 1.1. Угоди, і надає його в користування третім особам - Користувачам сайту.', 1),
('Користувач сайту — фізична особа (громадянин), що діє в своїх особистих інтересах, або уповноважений представник юридичної особи (організації), що діє в інтересах такої юридичної особи (організації), яка здійснює користування Інтернет-сайтом, зазначеним у пункті 1.1. цієї Угоди, зокрема використання окремих його функцій і інструментів.', 1);

insert into AgreementsItem(item, agree_title_item) values
('Предметом Угоди є надання Адміністрацією сайту Користувачам послуг з надання в користування Інтернет-сайту, зокрема передбачених на ньому функцій (додатків).', 2),
('Надання послуг з надання в користування Інтернет-сайту, зазначеного в пункті 1.1. Угоди, проводиться у відповідності з розділами Інтернет-сайту, що мають власне цільове призначення.', 2),
('Адміністрація сайту має право надавати Користувачам сайту інші види послуг шляхом включення в структуру сайту відповідних розділів або шляхом розширення функціонального обсягу розділів.', 2),
('Користувач вважається прийнявшим умови цієї Угоди користувача з моменту першого використання Інтернет-сайту (першого заходу на Інтернет-сайт).', 2),
('Адміністрація сайту здійснює збір, зберігання, обробку, передачу та інші аналогічні дії щодо інформації, що носить відкритий і загальнодоступний характер, зокрема про домени, про хостинг, про IP-адреси і т.д.', 2);

insert into AgreementsItem(item, agree_title_item) values
('Користувач Сайту не має права:', 4),
('Опубліковувати відгуки чи іншу інформацію, що порушують вимоги чинного законодавства, зокрема що містять погрози й образи, які дискредитують інших осіб, що порушують права громадян на приватне життя або публічний порядок, що носять характер непристойності; порушують в тій чи іншій мірі честь і гідність, права та захищені законом інтереси інших осіб; сприяють або містять заклики до розпалювання релігійної, расової або міжнаціональної ворожнечі, містять спроби розпалювання ворожнечі або заклики до насильства.', 4),
('Розміщувати на Сайті інформацію в формі реклами третіх осіб і/або товарів (робіт, послуг), що реалізуються (виконуються, надаються) даними особами.', 4),
('Поширювати або організовувати поширення на Сайті шкідливих комп''ютерних програм, файлів і посилань на них (віруси, трояни, «черв''яки» або будь-які інші програми, що заподіюють шкоду або призводять до видалення інформації на Сайті).', 4),
('Здійснювати автоматичний збір інформації з сайту за допомогою використання спеціальних комп''ютерних програм (парсерів).', 4);

create table if not exists AgreementsItem(
	id_agree_item int primary key auto_increment,
    item text(2500) not null,
    agree_title_item int,
    constraint agreeitem_fk foreign key (agree_title_item) references AgreementsTitle(id_agree_title)
);

select * from AgreementsTitle join AgreementsItem on AgreementsTitle.id_agree_title = AgreementsItem.agree_title_item;

